public class exercise4_12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=2;
		int dan=1; //1´Ü 2´Ü 3´Ü
		
		while(num<10){
			while(dan<4){
				System.out.printf("%d*%d=%d",num,dan,dan*num);
				System.out.print("\t");
				num++;
			}
		}
		
		

			System.out.println();
		}
	}


